import enum

class Direction(enum.Enum):
    RIGHT = 0
    DOWN = 1
    LEFT = 2
    UP = 3

# Maps a particular direction to a (deltaR, deltaC) tuple
# that corresponds to moving once in that direction.
# For example, to move "UP", you would move
# (-1 row, 0 col).
MOVEMENT: dict[Direction, tuple[int, int]] = {
    Direction.RIGHT: (0, 1),
    Direction.DOWN: (1, 0),
    Direction.LEFT: (0, -1),
    Direction.UP: (-1, 0)
}

class Room:
    matrix: list[list[str]]
    robotLocation = tuple[int, int]  # (row, col) robot is at

    def __init__(self, roomInput: str):
        # Initialize matrix representing room.
        self.matrix = [list(line) for line in roomInput.split("\n")]

        # Initialize robot position
        for r in range(len(self.matrix)):
            for c in range(len(self.matrix[0])):
                if self.matrix[r][c] == '@':
                    self.robotLocation = (r, c)

    def _inBounds(self, r, c):
        return r < len(self.matrix) and r >= 0 and c < len(self.matrix[0]) and c >= 0

    # Attempts to move the robot in the direction.
    # A robot can move in the direction if the space is empty, or if it's occupied by a block
    # that can be moved in the same direction.
    # If the robot cannot move in that direction, nothing changes.
    def moveRobot(self, direction: Direction):
        r, c = self.robotLocation
        deltaR, deltaC = MOVEMENT[direction]

        # Find the first non-block square.
        while (self._inBounds(r + deltaR, c + deltaC) and self.matrix[r+deltaR][c+deltaC] == 'O'):
            r += deltaR
            c += deltaC
        
        # If it's out of bounds or an immovable obstacle, don't move the robot.
        if (not self._inBounds(r + deltaR, c + deltaC) or self.matrix[r+deltaR][c+deltaC] == '#'):
            return
        
        # Otherwise, it's an empty space. Move the robot, and push all blocks in the way.
        originalRobotR, originalRobotC = self.robotLocation
        newRobotR, newRobotC = originalRobotR + deltaR, originalRobotC + deltaC
        self.matrix[originalRobotR][originalRobotC] = '.'
        self.matrix[r + deltaR][c+deltaC] = 'O'
        self.matrix[newRobotR][newRobotC] = '@'
        self.robotLocation = (newRobotR, newRobotC)

    def calculateBoxScore(self):
        score = 0
        for r in range(len(self.matrix)):
            for c in range(len(self.matrix[0])):
                if self.matrix[r][c] == 'O':
                    score += 100 * r + c
        return score

    def __str__(self):
        return "\n".join(["".join([c for c in row]) for row in self.matrix])

